import React from "react";
import { FcCheckmark } from "react-icons/fc";
import { FcCancel } from "react-icons/fc";
import styled from "styled-components";

const TaskList = React.memo((props) => {
  const { tasks, setTasks } = props;
  console.log(tasks);
  const changeColor = (task, index) => {
    const filterList = tasks.filter((list) => list.id !== task.id);
    setTasks(() => [
      ...filterL,
      (tasks[index] = { ...tasks[index], done: true }),
    ]);
  };
  const cancelColor = (index) => {

    setTasks(() => [...tasks, (tasks[index] = { ...tasks[index], done: false })]);
  };

  return (
    <ul>
      {tasks &&
        tasks.map((task, index) => {
          return (
            <Li key={task.id} done={task.done}>
              <Span>{task.title}</Span>
              {!task.done ? (
                <Click>
                  <FcCheckmark onClick={() => changeColor(task, index)} />
                </Click>
              ) : (
                <Click>
                  <FcCancel onClick={() => cancelColor(index)} />
                </Click>
              )}
            </Li>
          );
        })}
    </ul>
  );
});

const Li = styled.li`
  margin-bottom: 0.8em;
  background: ${(props) => (props.done ? "orange" : "green")};
`;
const Span = styled.span`
  margin: 0 1em 0 0;
`;
const Click = styled.span`
  cursor: pointer;
`;
export default React.memo(TaskList);
