import React, { useState } from "react";
import styled from "styled-components";

import NewTaskForm from "./NewTaskForm";
import TaskList from "./TastList";

const data = [
  { id: "1", title: "서비스 개발팀 회의 내용 정리", done: false },
  { id: "2", title: "PR 템플릿 만들기", done: false },
  { id: "3", title: "React UI 라이브러리 검토", done: false },
];

const App = () => {
  const [tasks, setTasks] = useState(data);

  let completeCount = 0;
  let inCompleteCount = 0;
  for (let i = 0; i < tasks.length; i++) {
    if (tasks[i].done === true) {
      completeCount++;
      inCompleteCount--;
    } else {
      inCompleteCount++;
      completeCount--;
    }
    if (completeCount < 0) {
      completeCount = 0;
    }
    // if (inCompleteCount < 0) {
    //   inCompleteCount = 0;
    // }
  }

  return (
    <Body>
      <h1>Hello TODO</h1>
      <NewTaskForm setTasks={setTasks} tasks={tasks} />
      <CountList>
        <span>전체: {tasks.length}</span>
        <span>완료: {completeCount}</span>
        <span>미완료: {inCompleteCount}</span>
      </CountList>
      <TaskList setTasks={setTasks} tasks={tasks} />
    </Body>
  );
};

const Body = styled.div`
  max-width: 300px;
  margin: auto;
`;

const CountList = styled.div`
  display: flex;
  gap: 8px;
`;
export default App;
