import { useState } from 'react';

const NewTaskForm = (props) => {
  const { setTasks, data } = props;
  const [list,setList] = useState();
  const myList = (e) => {
    setList
  }
  console.log(data);
  const addList = () => {
    setTasks((prev) => [...data, { id: "4", title: "hi", done: false }]);
  };
  return (
    <div>
      <input placeholder="새로울 할 일을 입력하세요." onChange={myList}/>
      <button onClick={addList}>등록</button>
    </div>
  );
};

export default NewTaskForm;
