import React, { useState } from "react";
import styled from "styled-components";

import NewTaskForm from "./NewTaskForm";
import TaskList from "./TastList";

const data = [
  { id: "1", title: "서비스 개발팀 회의 내용 정리", done: true },
  { id: "2", title: "PR 템플릿 만들기", done: false },
  { id: "3", title: "React UI 라이브러리 검토", done: false },
];

const App = () => {
  const [tasks, setTasks] = useState(data);

  return (
    <Div className="app">
      <h1>Hello TODO</h1>
      <NewTaskForm setTasks={setTasks} tasks={tasks} />
      <div style={{ display: "flex", gap: "8px" }}>
        <span>전체: {tasks.length}</span>
        <span>완료: 1</span>
        <span>미완료: 2</span>
      </div>
      <TaskList setTasks={setTasks} tasks={tasks} />
    </Div>
  );
};

const Div = styled.div`
  .app {
    max-width: 300px;
    margin: auto;
  }
`;
export default App;
